package 控制流程;

public class g {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
         for(int i =1 ; i<=9 ; i++)
         {
        	 for(int j=1;j<=i;j++)
        		 System.out.println(i+"*"+j+"="+(i*j)+"\t");
         }
         //打空行
         System.out.println();
         
         
	}

}
